import { useState } from "react"

export default function Input(){
    const [text,setText]=useState("");
    function Upper(){
        setText(text.toUpperCase());
    }
    function Lower(){
        setText(text.toLowerCase());
    }
    function Up(){
        setText(text.toUpperCase());
    }
    return(
        <>
             <h4 className="my-4">Enter Text : </h4>
             <div className="input-group">
  <textarea className="form-control" aria-label="With textarea" value={text} onChange={(e)=>setText(e.target.value)}></textarea>
</div>
<button type="button" className="btn btn-primary my-2" onClick={Upper}>Upper Case</button>
<button type="button" className="btn btn-primary my-2" onClick={Lower}>Lower Case</button>
        </>
    )
}
